import React from 'react';
import styles from './Burgers.module.css';
import { useDispatch, useSelector } from 'react-redux';

import { addItem } from '../../redux/slices/cartSlice';
import { useState } from 'react';


function Burgers({id, title, price, weight, image}) {
    const dispatch = useDispatch();
    const [open, setOpen] = useState(true);

    const onClickAdd = () => {
        const item = {
            id,
            image,
            title,
            weight,
            price
        }
        dispatch(addItem(item));
    }

    const Modal = ({open, setOpen, children}) => (
        <div className={`overlay animated ${open ? 'show': '' }`}>
                    <div className="modal">
                        <img onClick={() => setOpen(false)} src="./images/close.svg" alt="" />
                        {children}
                    </div>

                </div>
    )

    return (
        <div className={styles.burger}>
            <img onClick={() => setOpen(true)} src={image} alt="" />
                {open && <Modal open={open} setOpen={setOpen}>
                    <h2>это модально</h2>
                    </Modal>
                    }
                {/* <Modal open={open} setOpen={setOpen} /> */}
                
           
            <div className={styles.info}>
                <p className={styles.info_price}>{price}₽</p>
                <p className={styles.food_name}>{title}</p>
                <p className={styles.info_weight}>{weight}г</p>
            </div>
            <button onClick={onClickAdd} className={styles.add}>Добавить</button>
        </div>
    )
}

export default Burgers;
