import React, { useState } from "react";


import Categories from '../components/Categories/Categories';
import MainCart from '../components/MainCart/MainCart';
import Burgers from '../components/Burgers/Burgers';

import '../App.css';



const Home = () => {
    // const names = ['Бургеры', 'Закуски', 'Хот-доги', 'Комбо', 'Шаурма', 'Пицца', 'Вок', 'Десерты', 'Соусы']
    const [items, setItems] = React.useState([]);
    const [categoryId, setCategoryId] = React.useState(0);
    const [names, setNames] = React.useState([])

    React.useEffect(() => {
      fetch('https://6487473fbeba6297279060f8.mockapi.io/items?category=' + categoryId)
      .then((res) => res.json())
      .then((arr) => setItems(arr));
    }, [categoryId]);

    return (
        <>
            <Categories value={categoryId} onClickCategory={(i) => setCategoryId(i)} />
            <div className="main">
                <MainCart />
                <div className="content_food">

                        {/* <h3>{names}</h3> */}
                        
                    <div className="burgers">

                        {items.map(obj => (<Burgers key={obj.id} {...obj} />))}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
