import React, {useState} from "react";
import styles from './MainCart.module.css';
import { useDispatch, useSelector } from "react-redux/es/hooks/useSelector";

function MainCart({title, price, weight, image}) {
    const [foodCount, setFoodCount] = useState(0);
    const {items, totalPrice} = useSelector(state => state.cart);

    const onClickPlus = () => {
        setFoodCount(foodCount + 1);
    }

    const onClickMinus = () => {
        setFoodCount(foodCount - 1);
    }

    return (
    <div className={styles.main_cart}>
        <div className={styles.main_cart_header}>
            <h4>Корзина</h4>
            <button className={styles.sum}>{items.length}</button>
        </div>

        <p className={styles.line}></p>

        <div className={styles.cart_content}>

            <div className={styles.info_cart}>
                <img src={image} alt="" /> 
                <div className={styles.info_text}>
                    <p className={styles.info_food}>{title}</p>
                    <p className={styles.info_food_weight}>{weight}г</p>
                    <p className={styles.info_food}>{price}₽</p>
                </div>
            </div>
            <div className={styles.btn_count}>
                <button onClick={onClickMinus} className={styles.count_minus}>-</button>
                <button className={styles.count_number}>{foodCount}</button>
                <button onClick={onClickPlus} className={styles.count_plus}>+</button>
            </div>

        </div>

        <p className={styles.line}></p>

        <div className={styles.main_cart_footer}>
            <div className={styles.total}>
                <p className={styles.price}>Итого</p>
                <p className={styles.price}>{totalPrice} ₽</p>
            </div>
            <button className={styles.btn_order}>Оформить заказ</button>
            <div className={styles.delivery}>
                <img src="./images/delivery.svg" alt="" />
                <p className={styles.delivery_cart}>Бесплатная доставка</p>
            </div>
        </div>

    </div>
    )
}


export default MainCart;
