import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { setCategoryId } from "../redux/slices/filterSlice";
import Categories from '../components/Categories/Categories';
import MainCart from '../components/MainCart/MainCart';
import Burgers from '../components/Burgers/Burgers';

import '../App.css';




const Home = () => {
    const dispatch = useDispatch();
    const categoryId = useSelector((state) => state.filter.categoryId);
    const categoryNames = useSelector((state) => state.filter.categoryNames)

       // const names = ['Бургеры', 'Закуски', 'Хот-доги', 'Комбо', 'Шаурма', 'Пицца', 'Вок', 'Десерты', 'Соусы']
    const [items, setItems] = React.useState([]);
    const [names, setNames] = React.useState([]);

    const onChangeCategory = (id) => {
        dispatch(setCategoryId(id));
    };


    React.useEffect(() => {
      fetch('https://6487473fbeba6297279060f8.mockapi.io/items?category=' + categoryId + categoryNames)
      .then((res) => res.json())
      .then((arr) => setItems(arr));
    }, [categoryId]);

    return (
        <>
            <Categories value={categoryId} onChangeCategory={onChangeCategory} />
            <div className="main">
                <MainCart />
                <div className="content_food">
                {names.map(Names => (<h3>{Names.name}</h3>))}
                    {/* <h3>{names}</h3> */}
                        
                    <div className="burgers">

                        {items.map(obj => (<Burgers key={obj.id} {...obj} />))}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
