import React from 'react';
import styles from "./Arrange.module.css";
import { useState } from 'react';


const Arrange = () => {
    const [showAddress, setshowAddress] = useState(false);
    const [checked, setChecked] = useState(true);
    let content = null;

    function changeCheckbox() {
        setChecked(!checked);
    }

   if (showAddress){
    content = <>
    <form className={styles.address}>
            <input className={styles.basic} type="text" placeholder='Улица, дом, квартира' name="address"/>
            <form className={styles.optionally}>
                <input  className={styles.basic_optionally} type="text" placeholder='Этаж'  name="floor"/>
                <input className={styles.basic_optionally} type="text" placeholder='Домофон'  name="intercom"/>
            </form>
        </form>
    </>
   }
  return (
    <div>
        <form className={styles.basic}>
                <input className={styles.basic} type="text" placeholder='Ваше имя' name='text'/>
                <input className={styles.basic} type="phone" placeholder='Телефон' name='phone'/>
        </form>
                <div onClick={() => setshowAddress(false)} className={styles.delivery}>
                    <label  className={styles.label}>
                        <input type="radio" checked={checked} onChange={changeCheckbox} name="delivery" value="pickup" className={styles.radio} />
                        <span className={styles.fake}></span>
                        <span className={styles.text}>Самовывоз</span> 
                    </label>
                </div>
                <div  onClick={() => setshowAddress(true)} className={styles.delivery}>
                    <label className={styles.label}>
                        <input type="radio" onChange={changeCheckbox} name="delivery" value="delivery" className={styles.radio} />
                         <span className={styles.fake}></span>
                        <span className={styles.text}>Доставка</span>
                    </label>
                </div>
                {content}
    </div>
  )
}

export default Arrange;
