import React from "react";
import { useState } from "react";

import iconBurger from "./images/icon1.svg";
import iconSnackes from "./images/icon2.svg";
import iconHot from "./images/icon3.svg";
import iconCombo from "./images/icon4.svg";
import iconKebab from "./images/icon5.svg";
import iconPizza from "./images/icon6.svg";
import iconWok from "./images/icon7.svg";
import iconDesserts from "./images/icon8.svg";
import iconSauces from "./images/icon8.svg";

function Categories() {
  const [activeIndex, setActiveIndex] = useState(0);

  const categories = [
    {value: 'iconBurger + Бургеры'},
    {value: 'iconSnackes + Закуски'}, 
    {value: 'iconHot + Хот-доги'}, 
    {value: 'iconCombo + Комбо'}, 
    {value: 'iconKebab + Шаурма'}, 
    {value: 'iconPizza + Пицца'}, 
    {value: 'iconWok + Вок'}, 
    {value: 'iconDesserts + Десерты'}, 
    {value: 'iconSauces + Соусы'}
  ];

  const onClickCategory = (index) => {
    setActiveIndex(index);
  }

    return (
      <div className='categories'>
        <ul>
           {
            categories.map(value => (
              <li onClick={() => onClickCategory(0)} className={activeIndex === 0 ? 'active' : ''}>
                <img className='cat' src="./images/icon1.svg" alt="" />
                <a>{value}</a>
              </li> 
            ))
           }
        </ul>
      </div>
    )
  }

  export default Categories;
