import React, { useState } from "react";


import Categories from '../components/Categories/Categories';
import MainCart from '../components/MainCart/MainCart';
import Burgers from '../components/Burgers/Burgers';

import '../App.css';

const Home = () => {

    const [category, setCategory] = useState('ALL');
    const [allProducts, setAllProducts] = React.useState(products)

    useEffect(() => {
      if(category === 'BURGER'){
        const filteredProducts = products.filter(item => item.category === 'Burger')

        setAllProducts(filteredProducts)
      }

      if(category === 'SNACKES'){
        const filteredProducts = products.filter(item => item.category === 'Snack')

        setAllProducts(filteredProducts)
      }

      if(category === 'HOT_DOGS'){
        const filteredProducts = products.filter(item => item.category === 'Hot_dogs')

        setAllProducts(filteredProducts)
      }

      if(category === 'COMBO'){
        const filteredProducts = products.filter(item => item.category === 'Combo')

        setAllProducts(filteredProducts)
      }

      if(category === 'KEBABS'){
        const filteredProducts = products.filter(item => item.category === 'Kebab')

        setAllProducts(filteredProducts)
      }

      if(category === 'PIZZAS'){
        const filteredProducts = products.filter(item => item.category === 'Pizza')

        setAllProducts(filteredProducts)
      }

      if(category === 'WOKS'){
        const filteredProducts = products.filter(item => item.category === 'Wok')

        setAllProducts(filteredProducts)
      }

      if(category === 'DESSERTS'){
        const filteredProducts = products.filter(item => item.category === 'Dessert')

        setAllProducts(filteredProducts)
      }

      if(category === 'SAUCES'){
        const filteredProducts = products.filter(item => item.category === 'Sauce')

        setAllProducts(filteredProducts)
      }
    }, [category])
    


    // const [items, setItems] = React.useState([]);

    // React.useEffect(() => {
    //   fetch('https://6487473fbeba6297279060f8.mockapi.io/items')
    //   .then((res) => res.json())
    //   .then((arr) => setItems(arr));
    // }, []);

    return (
        <>
            <Categories />
            <div className="main">
                <MainCart />
                <div className="content_food">
                    <h3>Бургеры</h3>
                    <div className="burgers">
                        {items.map(obj => (<Burgers key={obj.id} {...obj} />))}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Home;
