const validation = (values) => {
    let errors ={};

    if(!values.name){
        errors.name="Name is required"
    }

    if(!values.phone){
        errors.name="Phone is required"
    }else {
        errors.phone="Phone is invalid"
    }

  return errors;
}

export default validation;
