import React, {useState} from "react";
import styles from './MainCart.module.css';
import { useDispatch, useSelector } from 'react-redux';
import CartItem from "../CartItem/CartItem";
import CartEmpty from "../CartEmpty/CartEmpty";
import { Form } from "react-router-dom";
import ModalOrder from "../ModalOrder/ModalOrder";
import PhotoModal from '../Modal/Modal';
import { addItem } from '../../redux/slices/cartSlice';

function MainCart({id, title, price, weight, image, description, ingredients, calories, name}) {
    const dispatch = useDispatch();
    const item = useSelector((state) => state.cart.items);
    const totalCount = item.reduce((sum, item) => sum + item.count, 0);
    const [modalVisible, setModalVisible] = useState(false)
    const onClickAdd = () => {
        const item = {
            id,
            image,
            title,
            weight,
            price,
            description,
            ingredients,
            calories,
            name
        }
        dispatch(addItem(item));
       
    }
   

    // const [foodCount, setFoodCount] = useState(0);
    const {items, totalPrice} = useSelector(state => state.cart);
    const [modalOrderVisible, setModalOrderVisible] = useState(false);

    if (!totalPrice){
        return <CartEmpty />;
    }
    
    return (
    <div className={styles.main_cart}>
        <div className={styles.main_cart_header}>
            <h4>Корзина</h4>
            <button className={styles.sum}>{totalCount}</button>
        </div>

        <p className={styles.line}></p>

        {
            items.map(item => <CartItem key={item.id} {...item} />)
        }

        {/* <div className={styles.cart_content}>



            <div className={styles.btn_count}>
                <button onClick={onClickMinus} className={styles.count_minus}>-</button>
                <button className={styles.count_number}>{foodCount}</button>
                <button onClick={onClickPlus} className={styles.count_plus}>+</button>
            </div>

        </div>

        <p className={styles.line}></p> */}

        <div className={styles.main_cart_footer}>
            <div className={styles.total}>
                <p className={styles.price}>Итого</p>
                <p className={styles.price}>{totalPrice} ₽</p>
            </div>
            <button onClick={() => setModalOrderVisible(true)} className={styles.btn_order}>Оформить заказ</button>
            <div className={styles.delivery}>
                <img src="./images/delivery.svg" alt="" />
                <p className={styles.delivery_cart}>Бесплатная доставка</p>
            </div>
        </div>
        <ModalOrder 
        Open={modalOrderVisible} 
        Close={() => setModalOrderVisible(false)}
        /> 
        <PhotoModal 
            title={title}
            image={image}
            description={description}
            ingredients={ingredients}
            weight={weight}
            calories={calories}
            price={price}
            onClick={onClickAdd}
            isOpen={modalVisible} 
            onClose={() => setModalVisible(false)} 
            name={name}

        />

    </div>
    )
}


export default MainCart;
