import React from 'react';
import './App.css';

import Header from './components/Header';
import Categories from './components/Categories';
import MainCart from './components/MainCart';
import Burgers from './components/Burgers';
import Footer from './components/Footer';

import burgers from './categories/burgers.json';
//import snackes from './categories/snackes.json';
//import hotDogs from './categories/hotDogs.json';


function App() {
  const [items, setItems] = React.useState([]);

  React.useEffect(() => {
    fetch('https://6487473fbeba6297279060f8.mockapi.io/items')
    .then((res) => res.json())
    .then((arr) => setItems(arr));
  }, []);



  return (
    <div className="App">
      <Header />
      <Categories />
      <div className="main">
        <MainCart />
      <div className="content_food">
        <h3>Бургеры</h3>
          <div className="burgers">
            {
              items.map(obj => (
                <Burgers key={obj.id} {...obj} />
              ))
            }
          </div>
       </div>
      </div>
        <Footer />
      </div>
  );
}

export default App;
