import React from "react";
import axios from "axios";
import { useState } from "react";
import { useParams } from "react-router-dom";


const DetailedProduct = () => {
    const [data, setData] = useState();
    const {id} = useParams();
    // const apiUrl = ('https://6487473fbeba6297279060f8.mockapi.io/items/' + id);
    // const fetchData = async () => {
    //     const response = await axios.get(apiUrl)

    //     setData(response.data)
    // }
    React.useEffect(() => {
        async function fetchData(){
            const {data} = await axios.get('https://6487473fbeba6297279060f8.mockapi.io/items/' + id);
            setData(data);
        }
        fetchData();
    }, []);
    return(
        <div className="fullDescription">
          <h3>{data.title}</h3>
           <div className="content">
                <img className="fullImg" src={data.image} />
                <div className="content_text">
                    <p>{data.description}</p>
                     <p>Состав:</p>
                     <p>{data.ingredients} </p>
                     <p>weight г</p>
                     <p>calories</p>
                 </div>
                <div className="content_footer">
                    <button className="Add">Добавить</button>
                   <div className="btn_count">
                         <button  className="count_minus">-</button>
                        <button className="count_number">0</button>
                        <button className="count_plus">+</button>
                     </div>
                     <h4>price p</h4>
                 </div>

            </div>
            
     </div>
    )
}

export default DetailedProduct;
