import React from "react";
import { useState } from "react";
import styles from './Categories.module.css'

function Categories() {
  // const [activeIndex, setActiveIndex] = useState("");

  const [category, setCategory] = React.useState('ALL');
  const [allProducts, setAllProducts] = React.useState(products);

    React.useEffect(() => {
      if(category === 'BURGER'){
        const filteredProducts = products.filter(item => item.category === 'Burger')

        setAllProducts(filteredProducts)
      }

      if(category === 'SNACKES'){
        const filteredProducts = products.filter(item => item.category === 'Snack')

        setAllProducts(filteredProducts)
      }

      if(category === 'HOT_DOGS'){
        const filteredProducts = products.filter(item => item.category === 'Hot_dogs')

        setAllProducts(filteredProducts)
      }

      if(category === 'COMBO'){
        const filteredProducts = products.filter(item => item.category === 'Combo')

        setAllProducts(filteredProducts)
      }

      if(category === 'KEBABS'){
        const filteredProducts = products.filter(item => item.category === 'Kebab')

        setAllProducts(filteredProducts)
      }

      if(category === 'PIZZAS'){
        const filteredProducts = products.filter(item => item.category === 'Pizza')

        setAllProducts(filteredProducts)
      }

      if(category === 'WOKS'){
        const filteredProducts = products.filter(item => item.category === 'Wok')

        setAllProducts(filteredProducts)
      }

      if(category === 'DESSERTS'){
        const filteredProducts = products.filter(item => item.category === 'Dessert')

        setAllProducts(filteredProducts)
      }

      if(category === 'SAUCES'){
        const filteredProducts = products.filter(item => item.category === 'Sauce')

        setAllProducts(filteredProducts)
      }
    }, [category]);

  const categories = [
    {img: "images/icon1.svg", name: 'Бургеры'},
    {img: "images/icon2.svg", name: 'Закуски'}, 
    {img: "images/icon3.svg", name: 'Хот-доги'}, 
    {img: "images/icon4.svg", name: 'Комбо'}, 
    {img: "images/icon5.svg", name: 'Шаурма'}, 
    {img: "images/icon6.svg", name: 'Пицца'}, 
    {img: "images/icon7.svg", name: 'Вок'}, 
    {img: "images/icon8.svg", name: 'Десерты'}, 
    {img: "images/icon9.svg", name: 'Соусы'}
  ];

  // const onClickCategory = (index) => {
  //   setActiveIndex(index);
  // }

    return (
      <div className='categories'>
        <ul>
           
            
              <li key={i} onClick={() => setCategory('BURGER')}>
                <img className={styles.cat} src={value.img} />
                <a>{value.name}</a>
              </li>
            {/* {categories.map((value, i) => (
              <li key={i} onClick={() => onClickCategory(i)} className={activeIndex === i ? styles.active : ''}>  
              <img className={styles.cat} src={value.img} />
              <a>{value.name}</a>
              </li> 
            ))} */}

           
        </ul>
      </div>
    )
  }

  export default Categories;
