import React from "react";
import { useParams } from "react-router-dom";
import '../App.css'

const FullProduct = () => {
    const [product, setProduct] = React.useState();
    const {id} = useParams();
    
    React.useEffect(() => {
        async function fetchProduct(){
            const data = await fetch('https://6487473fbeba6297279060f8.mockapi.io/items/' + id)
            .then((res) => res.json())
        .then((arr) => setItems(arr));
        //    .then(data => data.json());
            setProduct(data);
        }
        fetchProduct()
        
    }, []);

    if (!product){
        return 'Загрузка...';
    }

    return(
        <div className="container">
            
            <p>{product.title}</p>
            <img className="fullImg" src={product.image} alt="" />
            <h4>{product.price} p</h4>
        </div>
    )
}

export default FullProduct;
