    React.useEffect(() => {
     if (isMounted.current){
            const json = JSON.stringify(items);
            window.localStorage.setItem('cart', json);
        }
        isMounted.current = true;
    }, [items]);

    if (!totalPrice){
        return <CartEmpty />;
    }

    // const dropdown = () => {
        
