import React from "react";
import { useParams } from "react-router-dom";
import '../App.css'

const FullProduct = () => {
    const [product, setProduct] = React.useState();
    const {id} = useParams();
    
    React.useEffect(() => {
        async function fetchProduct(){
            const data = await fetch('https://6487473fbeba6297279060f8.mockapi.io/items/' + id)
            .then((data) => data.json())
        // .then((data) => setProduct(data));
        //    .then(data => data.json());
            setProduct(data);
        }
        fetchProduct()
        
    }, []);

    if (!product){
        return 'Загрузка...';
    }

    return(
        <div className="container">
            <h3>{product.title}</h3>
            <div className="content">
                <img className="fullImg" src={product.image} />
                <div className="content_text">
                <h2>ceg</h2>
                <p>Состав:</p>
                <p>{product.ingredients}</p>
                <p>{product.weigth} г</p>
                <p>{product.calories}</p>

                </div>
                <h4>{product.price} p</h4>
            </div>
            
        </div>
    )
}

export default FullProduct;
