import Modal from 'react-modal';

const PhotoModal = ({isOpen, onClose}) => {
    return(
        <Modal
        isOpen={visible}
        onRequestClose={onClose}
        >
            <div className='modalRoot'>
                <p>title</p>
                <img src="./images/icon5.svg" alt="" />
            </div>
        </Modal>
    )
}

export default PhotoModal;





