import React, { useEffect } from 'react';
import styles from "./Arrange.module.css";
import { useState } from 'react';
import validation from '../Validation/Validation';


const Arrange = () => {
    const [values, setValues] = useState({
        name: "",
        phone: "",
        address: "",
        floor: "",
        intercom: ""
    });
    const [showAddress, setshowAddress] = useState(false);
    const [checked, setChecked] = useState(true);
    let content = null;

    function changeCheckbox() {
        setChecked(!checked);
    }


    const handleChange = (event) => {
        setValues({
            ...values,
            [event.target.name]: event.target.value,
        })
    }

    

   if (showAddress){
    content = <>
    <form className={styles.address}>
            <input className={styles.basic} type="text" placeholder='Улица, дом, квартира' name="address" value={values.address} onChange={handleChange}/>
            <form className={styles.optionally}>
                <input  className={styles.basic_optionally} type="text" placeholder='Этаж'  name="floor" value={values.floor} onChange={handleChange}/>
                <input className={styles.basic_optionally} type="text" placeholder='Домофон'  name="intercom" value={values.intercom} onChange={handleChange}/>
            </form>
        </form>
    </>
   }

  return (
    <div>
        <div className={styles.forms}>
        <form className={styles.basic}>
                <input className={styles.basic} type="text" placeholder='Ваше имя' name='name' value={values.name} onChange={handleChange}/>
                <input className={styles.basic} type="phone" placeholder='Телефон' name='phone' value={values.phone} onChange={handleChange}/>
        </form>
                <div onClick={() => setshowAddress(false)} className={styles.delivery}>
                    <label  className={styles.label}>
                        <input type="radio" checked={checked} onChange={changeCheckbox} name="delivery" value="pickup" className={styles.radio} />
                        <span className={styles.fake}></span>
                        <span className={styles.text}>Самовывоз</span> 
                    </label>
                </div>
                <div  onClick={() => setshowAddress(true)} className={styles.delivery}>
                    <label className={styles.label}>
                        <input type="radio" onChange={changeCheckbox} name="delivery" value="delivery" className={styles.radio} />
                         <span className={styles.fake}></span>
                        <span className={styles.text}>Доставка</span>
                    </label>
                </div>
                {content}
                </div>
                <div className={styles.arrange}>
                    <button className={styles.arrangeBtn}>Оформить</button>
                </div>
    </div>
  )
}

export default Arrange;
