import React from "react";
import { useDispatch } from "react-redux";
import { addItem, minusItem } from "../../redux/slices/cartSlice";
import styles from './FullProduct.module.css';

const FullProduct = ({id, title, price, weight, image, ingredients, calories} ) => {
    const dispatch = useDispatch();


    
    // const onClickPlus = () => {
    //     dispatch(addItem({
    //         id,
    //     }));
    // }

    // const onClickMinus = () => {
    //     dispatch(minusItem(id))
    // }
    
    

    return(
        <div className="container">
    
            <h3>{title}</h3>
            <div className="content">
                <img className="fullImg" src={image} />
                <div className="content_text">
                    <h2>ceg</h2>
                    <p>Состав:</p>
                    <p>{ingredients}</p>
                    <p>{weight} г</p>
                    <p>{calories}</p>
                </div>
                <div className={styles.content_footer}>
                    <button className={styles.Add}>Добавить</button>
                    <div className={styles.btn_count}>
                        <button  className={styles.count_minus}>-</button>
                        <button className={styles.count_number}>0</button>
                        <button className={styles.count_plus}>+</button>
                    </div>
                    <h4>{price} p</h4>
                </div>

            </div>
            
        </div>
    )
}

export default FullProduct;
