import React from "react";
import { useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const DetailedProduct = () => {
    const [data, setData] = useState(null);
    const {id} = useParams();
    const apiUrl = ('https://6487473fbeba6297279060f8.mockapi.io/items/' + id);
    const fetchData = async () => {
        const response = await axios.get(apiUrl)

        setData(response.data)
    }
    return(
        <div className="container">
            <button className="fetch-button" onClick={fetchData}>
          Fetch Data
        </button>
          <h3>{data.title}</h3>
           <div className="content">
                <img className="fullImg" src="/images/icon1.svg" />
               <div className="content_text">
                   <h2>ceg</h2>
                     <p>Состав:</p>
                     <p>jmm </p>
                     <p>weight г</p>
                     <p>calories</p>
                 </div>
                <div className="content_footer">
                    <button className="Add">Добавить</button>
                   <div className="btn_count">
                         <button  className="count_minus">-</button>
                        <button className="count_number">0</button>
                        <button className="count_plus">+</button>
                     </div>
                     <h4>price p</h4>
                 </div>

            </div>
            
     </div>
    )
}

export default DetailedProduct;
